<script type="text/javascript" language="javascript">
	function nhantin(){
		if(isEmpty($('#email_nhantin').val(), "<?=_nhapemailcuaban?>"))
		{
			$('#email_nhantin').focus();
			return false;
		}
		if(isEmail($('#email_nhantin').val(), "<?=_emailkhonghople?>"))
		{
			$('#email_nhantin').focus();
			return false;
		}	
		document.frm_dknt.submit();
	}
</script>
